resource 'XOPI' (1100) {
    XOP_VERSION,	// Version number of host XOP system.
    DEV_SYS_CODE,	// Code for development system used to make XOP.
    H_MENUS,		  // XOP requires hierarchical menus.
    UNUSED,		    // XDK for MacOS does not support 68k XOP : set to zero.
    UNUSED,		    // Obsolete : set to zero. 
};
